#!/usr/bin/env node
import VSLParser from '../vsl/parser/vslparser';
import VSLTokenizer from '../vsl/parser/vsltokenizer';
import VSLTransformer from '../vsl/transform/transformers/vsltransformer';

import readline from 'readline';
import util from 'util';
import colors from 'colors';
import { readFileSync } from 'fs';
import minimist from 'minimist';
import pjson from '../../package.json';

const print = console.log,
    stdin = process.stdin,
    argv = minimist(process.argv.slice(2),  {
        alias: {
            help: 'h',
            parserrepl: 'p',
            tokenizerrepl: 't',
            transformrepl: 'r',
            code: 'c'
        },
        booleans: [
        ],
        default: {
        }
    }),
    tokenizer = new VSLTokenizer();

let code = '',
    parser = new VSLParser(),
    rl;

stdin.setEncoding('utf-8');

function display (output) {
    print(util.inspect(output, {showHidden: false, depth: null, colors: true}));
}

function feed (code) {
    let result;
    if ((result = parser.feed(code))) {
        return result;
    } else {
        if (result === null)
            return;
        print("Syntax Error".red + ": ");
        //TODO: tokenizer now returns formatted error
        print(parser.error.message);
    }
}

function exit () {
    process.exit();
}

function help () {
    print(`VSL: Versatile Scripting Language, v${pjson.version}
Usage: vsl PATH_TO_VSL_FILE

Options:
    -h, --help          Show help

Debug REPLs:
    -p, --parserrepl    Start parser REPL
    -t, --tokenizerrepl Start tokenizer REPL
    -r, --transformrepl Start parser -> VSLTransformer REPL
    -c, --code CODE     Parse CODE`) || exit();
}

if (argv.help)
    help();

if (argv._.length)
    display(feed(readFileSync(argv._[0]))) || exit();

if (argv.code)
    display(feed(argv.code)) || exit();

if (argv.p || argv.t || argv.r)
    rl = readline.createInterface({
        input: process.stdin,
        output: process.stdout
    });

if (argv.r) {
    function prompt() {
        rl.setPrompt('vsl:transform> '.red.bold);
        rl.prompt();
    }
    prompt();
    let feeding = false;
    let transformer = new VSLTransformer();
    rl.on('line', function (input) {
        if (input === 'exit')
    		exit(rl.close());
        if (feeding === true) {
            let result = feed(input);
            if (typeof result === 'undefined') {
                feeding = false;
                prompt();
            } else if (result.length > 0) {
                feeding = false;
                display(result);
                prompt();
            } else
                rl.prompt();
            return;
        }
        parser = new VSLParser();
    	let result = feed(input);
    	transformer.queue(result.results);
    	if (typeof result === 'undefined')
    	    return prompt();
    	// check if got any tokens at all
    	// i.e. skip if all comments or no input
    	if (result.tokens && result.length < 1) {
    	    feeding = true;
    	    rl.setPrompt('>>>>>>>>>>>>> '.bold);
    	    return rl.prompt();
    	}
    	display(result);
        prompt();
    });
}

if (argv.p) {
    function prompt() {
        rl.setPrompt('vsl:parser> '.red.bold);
        rl.prompt();
    }
    prompt();
    let feeding = false;
    rl.on('line', function (input) {
        if (input === 'exit')
    		exit(rl.close());
        if (feeding === true) {
            let result = feed(input);
            if (typeof result === 'undefined') {
                feeding = false;
                prompt();
            } else if (result.length > 0) {
                feeding = false;
                display(result);
                prompt();
            } else
                rl.prompt();
            return;
        }
        parser = new VSLParser();
    	let result = feed(input);
    	if (typeof result === 'undefined')
    	    return prompt();
    	// check if got any tokens at all
    	// i.e. skip if all comments or no imput
    	if (result.tokens && result.length < 1) {
    	    feeding = true;
    	    rl.setPrompt('>>>>>>>>>>> '.bold);
    	    return rl.prompt();
    	}
    	display(result);
        prompt();
    });
}

if (argv.t) {
    rl.setPrompt('vsl:tokenizer> '.red.bold);
    rl.prompt();
    rl.on('line', function(input) {
        if (input === 'exit')
    		rl.close() || exit();
    	display(tokenizer.tokenize(input));
        rl.prompt();
    });
}

if (!argv.p && !argv.t && !argv.r) {
    stdin.on('readable', function () {
        let chunk = process.stdin.read();
        if (chunk)
            code += chunk;
        else if (!code)
            help();
        while ((chunk = process.stdin.read()))
            code += chunk;
    });
    stdin.on('end', function () {
        display(feed(code));
        exit();
    });
}

import colors from 'colors';

function leftPad (str, len) {
    var pad = len - str.length, i = "";
    if (pad <= 0) return str;
    while(pad--) i += " ";
    return i + str;
}

/**
 * Highlights a series of lines & indices as specified by indicator
 * 
 * @param {Object} indicators - The result from indicator(code:index:)
 * @param {bool} [highlight=true] - To highlight output with ANSI
 */
export default function highlight(indicators, highlight: bool = true) {
    var lines = indicators.lines, line,
        start = indicators.startIndex, k = 0,
        end = start + indicators.relativeEnd,
        suffix = " | ",
        indicator = "^",
        l = indicators.start;

    if (highlight) {
        suffix = suffix.red
        indicator = indicator.red.bold
    }

    var maxLen = ((indicators.start + lines.length) + suffix).length;

    for (var i = 0; i < lines.length; i++) {
        lines[i] = leftPad(l++ + suffix, maxLen) + lines[i];

        if (i >= indicators.relativeLine && k < end) {
            line = lines[i];
            lines.splice(++i, 0, leftPad(suffix, maxLen));

            for(let j = 0; j < line.length && k < end; j++, k++) {
                // if (highlight) lines[i - 1][i] = lines[i - 1][i].bold;

                if (k >= start) lines[i] += indicator;
                else lines[i] += " ";
            }
        }
    }

    return lines;
}

/**
 * Highlights a given index range given a code
 *
 * @param {string} code - The specific code which will be used to determine the
 *                        create the index highlight
 * @param {number[]} index - An array in the form of [start, end] which specify
 *                           the indexes to highlight
 * @param {boolean} [highlight=false] - Highlight with ANSI codes the output
 * 
 * @return {Object} contains the needed indices and lines
 * @property {string[]} lines the lines to view
 * @property {number} relativeLine the relative index of the starting line
 * @property {number} startIndex the index to start highlighting at
 * @property {number} relativeEnd the relative end to startIndex to end highlight
 */
export default function bound (code: string, index: number) {
    let i = 0,
        lines = [],
        lineNumber = 0,
        previous = '',
        numberOfBoundingLines = 2,
        keepCurrentLine = false,
        line = 0,
        relativeLine = -1,
        currentLineIndex = 0,
        start = 0,
        startIndex = 0;

    while (i <= code.length) {
        if (code[i] === '\n' || i === code.length) {
            // If excess lines & k is false (keep)
            if (lines.length >= numberOfBoundingLines && !keepCurrentLine) {
                lines.shift();
                start++;
            }

            if (relativeLine < 0 && keepCurrentLine)
                relativeLine = lines.length;
            lines.push(previous);

            lineNumber++;
            currentLineIndex = i + 1;

            // Stop if > t
            if (keepCurrentLine && lineNumber - line > numberOfBoundingLines)
                break;

            previous = '';
        } else {
            previous += code[i];
        }

        if (i === index[0]) {
            startIndex = i - currentLineIndex;
            keepCurrentLine = true;
        }

        if (i < index[1])
            line = lineNumber;

        i++;
    }

    return {
        lines,
        start,
        relativeLine,
        startIndex: startIndex,
        relativeEnd: index[1] - index[0]
    };
}

import Node from './node';

/**
 * Matches any assignment type.
 * 
 * This matches any generic binary expression.
 */
export default class AssignmentStatement extends Node {

    /**
     * Creates a wrapper ExperssionStatement
     * 
     * @param {AssignmentType} type The assignment type
     * @param {TypedIdentifier} identifier The variable's identifier & type
     * @param {Expression} value The variable's inital value
     * @param {Object} position a position from nearley
     */
    constructor (type: AssignmentType, identifier: TypedIdentifier, value: Expression, position: Object) {
        super(position);

        /** @type {AssignmentType} */
        this.type = type;
        /** @type {TypedIdentifier} */
        this.identifier = identifier;
        /** @type {Expression} */
        this.value = value;
    }

    /** @override */
    get identifierPath() {
        return this.identifier;
    }

    /** @override */
    get children() {
        return ['lhs', 'rhs'];
    }
}

/**
 * Specifies an assignment type
 * @enum {number}
 */
const AssignmentType = {
    Variable: 0,
    Constant: 1
};

export default AssignmentType;

import Node from './node';

/**
 * Matches a binary expression
 * 
 * See Also: `UnaryExpression`
 * 
 * This matches any generic binary expression.
 */
export default class BinaryExpression extends Node {

    /**
     * Creates a wrapper ExperssionStatement
     * 
     * @param {Expression} lhs left-hand side of the expression
     * @param {Expression} rhs right-hand side of the expression
     * @param {string} operator the operator for the expression
     * @param {Object} position a position from nearley
     */
    constructor (lhs: Expression, rhs: Expression, operator: string, position: Object) {
        super(position);

        /** @type {Expression} */
        this.lhs = lhs;
        /** @type {Expression} */
        this.rhs = rhs;
        /** @type {string} */
        this.op = operator;
    }

    /** @override */
    get children() {
        return ['lhs', 'rhs'];
    }
}

import Node from './node';

/**
 * Wraps a class
 * 
 */
export default class ClassStatement extends Node {

    /**
     * Constructs a generic function statement
     * 
     * @param {string[]} access - The access modifiers of the node
     * @param {Identifier[]} superclass - The superclasses to inherit or implement
     * @param {Node[]} statements - The class's body.
     * @param {Object} position - a position from nearley
     */
    constructor(
        access: string[],
        name: Identifier,
        superclasses: Identifier[],
        statements: [],
        position: Object
    ) {
        super(position);

        /** @type {string} */
        this.access = access;
        this.name = name;
        this.superclasses = superclasses;
        this.statements = statements;
    }

    /** @override */
    get identifierPath() {
        return this.name;
    }

    /** @override */
    get children() {
        return ['name', 'superclasses', 'statements'];
    }
}

import Node from './node';

/**
 * Contains a series of statements.
 * 
 * This is used by the scope generator to determine where a new scope starts. If
 * this does not fit your needs but you need a scope, subclass this and call the
 * `statements:` field with null
 */
export default class CodeBlock extends Node {

    /**
     * Creates a scoped series of expressions
     * 
     * @param {Node[]} statements - the statements
     * @param {Object} position - a position from nearley
     */
    constructor (statements: any[], position: Object) {
        super(position);

        /** @type {Expression[]} */
        this.statements = statements;

        /**
         * This is a hashmap describing the child scope and it's variables. It
         * contains both variable information. This is primarially to be used by
         * `ScopeTransformer`, and all information in custom transformations
         * should be accessable through an `ASTTool`
         * 
         * @type {Map}
         */
        this.scope = new Map();
    }

    /** @override */
    get children() {
        return ['statements'];
    }
}

import Node from './node';

/**
 * Matches an expression as a top-level statement
 */
export default class CommandChain extends Node {

    /**
     * Creates a wrapper ExperssionStatement
     * 
     * @param {Expression[]} expression the primary expression
     * @param {Object} position a position from nearley
     */
    constructor (expressions: any[], position: Object) {
        super(position);

        /** @type {Expression[]} */
        this.expressions = expressions;
