        /** @type {string} */
        this.access = access;
        this.name = name;
        this.args = args;
        this.returnType = returnType;
        this.statements = statements;
    }

    /** @override */
    get identifierPath() {
        return this.name;
    }

    /** @override */
    get children() {
        return ['name', 'args', 'returnType', 'statements'];
    }
}

