/**
 * This matches any simple literal.
 * 
 * This excludes more complex literal types such as lambdas and switches.
 * If it can be expressed in a single string this class will wrap it, else
 *  consider a more complex class. 
 * 
 * The primary classes using this are:
 *  - String
 *  - Number
 *  - Boolean?
 */
export default class Literal extends Node {

    /**
     * Creates a wrapper for literals
     * 
     * @param {string} literal - the literal string value of the literal
     * @param {number} type - The literal type as from a TokenType
     * @param {Object} position - a position from nearley
     */
    constructor (literal: string, type: number, position: Object) {
        super(position);

        /** @type {string} */
        this.literal = literal;

        /** @type {VSLTokenType} */
        this.type = type;
    }

    /** @override */
    get children() {
        return null;
    }
}

/**
 * Node class for AST.
 */
export default class Node {

    /**
     * Creates a new Node object.
     */
    constructor(position: Object) {
        //if (process.env["VSL_ENV"] != "dev_debug")
        //    this.position = position;

        /**
         * If exists, references the closest scope. Use an ASTTool to perform
         * variable lookups
         * 
         * @type {?CodeBlock}
         */
        this.parentScope = null;

        /**
         * If exists, a transverser will set this to the parent node or wrapping
         * container.
         * 
         * @type {?(Node | Node[])}
         */
        this.parentNode = null;
    }

    /**
     * Generates an string representing the AST from a generator.
     * 
     * The string should use the passed generator to create the outputting
     * string so padding is correct.
     * 
     * @abstract
     * @param {Generator} generator the target generator
     **/
     generate(generator: Generator) {
         throw new TypeError("generate(generator:) must be overriden");
     }

    /**
     * Returns all the children of a node. The order must be consistent
     * @type {?Node[]}
     */
    get children() {
        throw new Error("Must implement Node#children");
    }

    /**
     * Specifies the field name which declares a variable.
     * @type {?(Identifier | TypedIdentifier)}
     */
    get identifierPath() {
        return null;
    }

    /**
     * Returns the string representation of the Node.
     * @param {string} padding String to add to the left of the tree
     * @return {string} Tree representation of this node.
     */
    toAST (padding: String='') {
        let children = this.children;
        if (this.unbox)
            return padding + '"' + this.children + '"\n';
        let result = '';
        if (this.type === NodeType.BinaryExpression || this.type === NodeType.UnaryExpression)
            result = result + padding + operator_names[this.operator_type] + '\n';
        else
            result = result + padding + this.constructor.name + '\n';
        if (padding.length >= 3) {
            let last_chars = padding.slice(-1);
            if (last_chars === '')
                padding = padding.slice(0, -1) + '';
            else if (last_chars === '')
                padding = padding.slice(0, -1) + ' ';
        }
        if (typeof this.children === 'string')
            result = result + padding + '"' + this.children + '"\n';
        else if (this.children.length !== 0) {
            let new_padding = padding + '';
            for (let i = 0; i < this.children.length - 1; i++)
                result += this.children[i].toAST(new_padding);
            new_padding = padding + '';
            let item = this.children[this.children.length - 1];
            result += item.toAST(new_padding);
        }
        return result;
    }

}

import Node from './node';

/**
 * Matches a property expression.
 * 
 * A property expression is anything in the form `head tail`.
 * 
 * A tail can have various markers including:
 *  - accessors through `.`
 *  - subscript `[ ... ]`
 *  - function calls `( ... )`
 *  - etc
 * 
 * Make sure you specify tail still if it doesn't exist
 */
export default class PropertyExpression extends Node {

    /**
     * Creates a wrapper ExperssionStatement
     * 
     * @param {Expression} expression the primary expression
     * @param {Object} position a position from nearley
     */
    constructor (head: any, tail: any, position: Object) {
        super(position);

        /** @type {Expression} */
        this.head = head;

        /** @type {Identifier[]|Subscript[]|FunctionCall[]} */
        this.tail = tail;
    }

    /** @override */
    get children () {
        return ['head', 'tail'];
    }
}

import Node from './node';

/**
 * Matches a subscript expression inside a PropertyExpression
 * 
 * @example
 * head[tail]
 */

export default class Subscript extends Node {
    /**
     * Creates a subscript
     * 
     * @param {Expression} expression the provided expression
     * @param {Object} position a position from nearley
     */
    constructor (expression: any, position: Object) {
        super(position);

        /** @type {Expression} */
        this.expression = expression;
    }

    /** @override */
    get children () {
        return ['expression'];
    }
}

